mapper.select:
select count(*) from tbl_employee E left join tbl_dept_name D on E.fk_dept_no = D.dept_no left join tbl_position_sort P on E.fk_position_no = P.position_no left join tbl_board_grant B on E.fk_board_grant_no = B.board_grant_no left join tbl_site_grant S on E.fk_site_grant_no = S.site_grant_no where 1=1
and substr(emp_name, 1, 1) >= #{range1} and substr(emp_name, 1, 1) < #{range2}
and substr(emp_name, 1, 1) >= #{range1}
and (upper(emp_name) like '%'||upper(#{contactInput})||'%' or upper(dept_name) like '%'||upper(#{contactInput})||'%' or upper(position_name) like '%'||upper(#{contactInput})||'%' )

select emp_no, dept_name, position_name, board_grant_name, site_grant_name , emp_name, corp_phone, postcode, address, detail_address, extra_address, mobile, email, firstday, photo_route, photo_name, status from ( select row_number() over(order by emp_name) AS rno , emp_no, dept_name, position_name, board_grant_name, site_grant_name , emp_name, corp_phone, postcode, address, detail_address, extra_address, mobile, email, firstday, photo_route, photo_name, status from tbl_employee E left join tbl_dept_name D on E.fk_dept_no = D.dept_no left join tbl_position_sort P on E.fk_position_no = P.position_no left join tbl_board_grant B on E.fk_board_grant_no = B.board_grant_no left join tbl_site_grant S on E.fk_site_grant_no = S.site_grant_no where 1=1
and substr(emp_name, 1, 1) >= #{range1} and substr(emp_name, 1, 1) < #{range2}
and substr(emp_name, 1, 1) >= #{range1}
and (upper(emp_name) like '%'||upper(#{contactInput})||'%' or upper(dept_name) like '%'||upper(#{contactInput})||'%' or upper(position_name) like '%'||upper(#{contactInput})||'%' )
) V
where rno between #{startRno} and #{endRno}
